import { ScrollArea } from "@/components/ui/scroll-area";
import coachAvatar from "@assets/generated_images/friendly_animated_avatar_of_a_leadership_coach..png";

type Message = {
  id: string;
  role: "user" | "assistant";
  content: string;
  timestamp: Date;
};

export function AICoach() {
  const [messages, setMessages] = useState<Message[]>([
    {
      id: "1",
      role: "assistant",
      content: "Hi! I'm LionLead, your personal leadership coach. I can help you prepare for 1:1s, draft difficult emails, or just vent about a tough day. What's on your mind?",
      timestamp: new Date()
    }
  ]);
  const [input, setInput] = useState("");
  const [isTyping, setIsTyping] = useState(false);
  const scrollRef = useRef<HTMLDivElement>(null);

  useEffect(() => {
    if (scrollRef.current) {
      scrollRef.current.scrollIntoView({ behavior: "smooth" });
    }
  }, [messages, isTyping]);

  const handleSend = () => {
    if (!input.trim()) return;

    const userMsg: Message = {
      id: Date.now().toString(),
      role: "user",
      content: input,
      timestamp: new Date()
    };

    setMessages(prev => [...prev, userMsg]);
    setInput("");
    setIsTyping(true);

    // Mock response
    setTimeout(() => {
      const responses = [
        "That sounds like a common challenge for new leaders. Have you tried setting clearer expectations upfront?",
        "It's great that you're thinking about this. What's the outcome you're hoping for in that conversation?",
        "Communication is key here. Maybe try framing it from the perspective of team goals rather than individual performance.",
        "I can help you draft a script for that. Would you like to try role-playing it?"
      ];
      const randomResponse = responses[Math.floor(Math.random() * responses.length)];

      const aiMsg: Message = {
        id: (Date.now() + 1).toString(),
        role: "assistant",
        content: randomResponse,
        timestamp: new Date()
      };

      setMessages(prev => [...prev, aiMsg]);
      setIsTyping(false);
    }, 1500);
  };

  return (
    <div className="h-[600px] flex flex-col bg-card rounded-[2rem] shadow-xl overflow-hidden border border-border/50">
      {/* Header */}
      <div className="p-6 border-b border-border flex items-center gap-4 bg-muted/30">
        <div className="w-12 h-12 rounded-full overflow-hidden border-2 border-white shadow-sm">
          <img src={coachAvatar} alt="Coach" className="w-full h-full object-cover" />
        </div>
        <div>
          <h3 className="font-heading font-bold text-lg">LionLead Coach</h3>
          <div className="flex items-center gap-2 text-xs text-muted-foreground">
            <span className="w-2 h-2 rounded-full bg-green-500 animate-pulse"></span>
            Online & Ready to help
          </div>
        </div>
      </div>

      {/* Messages */}
      <ScrollArea className="flex-1 p-6">
        <div className="space-y-6">
          {messages.map((msg) => (
            <div
              key={msg.id}
              className={`flex gap-3 ${msg.role === "user" ? "flex-row-reverse" : "flex-row"}`}
            >
              <div className={`w-8 h-8 rounded-full flex items-center justify-center flex-shrink-0 ${msg.role === "user" ? "bg-primary/20 text-primary-foreground" : "bg-muted text-muted-foreground"}`}>
                {msg.role === "user" ? <User className="w-5 h-5 text-primary" /> : <Bot className="w-5 h-5" />}
              </div>
              <div
                className={`max-w-[80%] p-4 rounded-2xl text-base leading-relaxed ${
                  msg.role === "user"
                    ? "bg-primary text-primary-foreground rounded-tr-none shadow-md"
                    : "bg-secondary text-secondary-foreground rounded-tl-none"
                }`}
              >
                {msg.content}
              </div>
            </div>
          ))}
          {isTyping && (
            <div className="flex gap-3">
              <div className="w-8 h-8 rounded-full bg-muted flex items-center justify-center">
                 <Bot className="w-5 h-5 text-muted-foreground" />
              </div>
              <div className="bg-secondary p-4 rounded-2xl rounded-tl-none flex gap-1 items-center h-12">
                <span className="w-2 h-2 bg-foreground/30 rounded-full animate-bounce" style={{ animationDelay: "0ms" }}></span>
                <span className="w-2 h-2 bg-foreground/30 rounded-full animate-bounce" style={{ animationDelay: "150ms" }}></span>
                <span className="w-2 h-2 bg-foreground/30 rounded-full animate-bounce" style={{ animationDelay: "300ms" }}></span>
              </div>
            </div>
          )}
          <div ref={scrollRef} />
        </div>
      </ScrollArea>

      {/* Input */}
      <div className="p-4 bg-muted/30 border-t border-border">
        <div className="relative flex items-center">
          <Input
            value={input}
            onChange={(e) => setInput(e.target.value)}
            onKeyDown={(e) => e.key === "Enter" && handleSend()}
            placeholder="Ask for advice, scripts, or feedback..."
            className="h-14 pl-6 pr-14 rounded-full border-2 border-border focus:border-primary bg-background text-lg shadow-sm"
          />
          <Button
            onClick={handleSend}
            size="icon"
            className="absolute right-2 w-10 h-10 rounded-full bg-primary text-primary-foreground hover:bg-primary/90"
          >
            <Send className="w-5 h-5" />
          </Button>
        </div>
      </div>
    </div>
  );
}

