import { useState } from "react";
import { motion, AnimatePresence } from "framer-motion";
import { JOURNEY_DATA, type DayContent, type UserProfile } from "@/lib/mockData";
import { Check, ChevronLeft, ChevronRight, Lock, Send, PartyPopper, Trophy } from "lucide-react";
import { Button } from "@/components/ui/button";
import { Progress } from "@/components/ui/progress";
import { Textarea } from "@/components/ui/textarea";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogDescription } from "@/components/ui/dialog";
import { useToast } from "@/hooks/use-toast";

interface JourneyViewProps {
  user: UserProfile;
  onUpdateUser: (user: UserProfile) => void;
}

export function JourneyView({ user, onUpdateUser }: JourneyViewProps) {
  const [selectedDay, setSelectedDay] = useState<number>(user.currentDay);
  const [reflection, setReflection] = useState("");
  const [showCelebration, setShowCelebration] = useState(false);
  const { toast } = useToast();

  const currentContent = JOURNEY_DATA.find((d) => d.day === selectedDay) || JOURNEY_DATA[0];
  const isLocked = selectedDay > user.currentDay;
  const isCompleted = user.completedDays.includes(selectedDay);

  const handleCompleteDay = () => {
    if (isCompleted) return;

    const newCompleted = [...user.completedDays, selectedDay];
    const nextDay = Math.min(30, user.currentDay + 1);

    // Only advance current day if we just completed the current day
    const newCurrentDay = selectedDay === user.currentDay ? nextDay : user.currentDay;

    onUpdateUser({
      ...user,
      completedDays: newCompleted,
      currentDay: newCurrentDay,
    });

    if (selectedDay === 30) {
        setShowCelebration(true);
    } else {
        toast({
          title: "Day Completed! üéâ",
          description: "Great work. You're building strong leadership habits.",
          duration: 3000,
        });
    }
  };

  const handleSaveReflection = () => {
    onUpdateUser({
      ...user,
      reflections: {
        ...user.reflections,
        [selectedDay]: reflection
      }
    });
    toast({
      title: "Reflection Saved",
      description: "Your thoughts have been captured.",
    });
  };

  return (
    <div className="space-y-8 max-w-4xl mx-auto pb-12">
      {/* Celebration Modal */}
      <Dialog open={showCelebration} onOpenChange={setShowCelebration}>
        <DialogContent className="bg-card border-none rounded-[2rem] max-w-md text-center p-12">
          <div className="w-24 h-24 bg-yellow-100 rounded-full flex items-center justify-center mx-auto mb-6 animate-bounce">
            <Trophy className="w-12 h-12 text-yellow-600" />
          </div>
          <DialogTitle className="text-3xl font-heading font-bold mb-4">
            Congratulations! ü¶Å
          </DialogTitle>
          <DialogDescription className="text-lg text-muted-foreground mb-8">
            You've completed your first 30 days as a leader. You've built new habits, reflected on your growth, and taken action.
          </DialogDescription>
          <Button 
            onClick={() => setShowCelebration(false)}
            className="w-full h-14 rounded-full text-lg font-bold bg-primary text-primary-foreground hover:bg-primary/90 shadow-lg"
          >
            Continue Leading
          </Button>
        </DialogContent>
      </Dialog>

      {/* Header & Progress */}
      <div className="space-y-4">
        <div className="flex items-center justify-between">
          <div>
            <h2 className="text-3xl font-heading font-bold text-foreground">Your 30-Day Journey</h2>
            <p className="text-muted-foreground">Day {user.currentDay} of 30</p>
          </div>
          <div className="text-right">
            <span className="text-2xl font-bold text-primary">{Math.round((user.completedDays.length / 30) * 100)}%</span>
          </div>
        </div>
        <Progress value={(user.completedDays.length / 30) * 100} className="h-4 rounded-full bg-secondary [&>div]:bg-gradient-to-r [&>div]:from-primary [&>div]:to-orange-400" />
      </div>

      {/* Day Navigation */}
      <div className="flex items-center gap-4 overflow-x-auto py-4 no-scrollbar mask-fade">
        {JOURNEY_DATA.map((day) => (
          <button
            key={day.day}
            onClick={() => day.day <= user.currentDay && setSelectedDay(day.day)}
            disabled={day.day > user.currentDay}
            className={`
              flex-shrink-0 w-12 h-12 rounded-full flex items-center justify-center font-bold transition-all
              ${selectedDay === day.day 
                ? "bg-foreground text-background scale-110 shadow-lg ring-2 ring-primary ring-offset-2" 
                : day.day <= user.currentDay
                  ? user.completedDays.includes(day.day)
                    ? "bg-primary text-primary-foreground hover:bg-primary/90"
                    : "bg-card text-foreground hover:bg-secondary border border-border"
                  : "bg-muted text-muted-foreground opacity-50 cursor-not-allowed"
              }
            `}
          >
            {user.completedDays.includes(day.day) && day.day !== selectedDay ? (
              <Check className="w-5 h-5" />
            ) : (
              day.day
            )}
          </button>
        ))}
      </div>

      {/* Content Card */}
      <AnimatePresence mode="wait">
        <motion.div
          key={selectedDay}
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          exit={{ opacity: 0, y: -20 }}
          transition={{ duration: 0.3 }}
        >
          <Card className="border-none shadow-xl bg-card overflow-hidden rounded-[2rem]">
            <div className="h-2 bg-gradient-to-r from-primary to-orange-400 w-full" />
            <CardHeader className="pb-2">
              <div className="flex justify-between items-start">
                <div>
                  <Badge variant="secondary" className="mb-2 bg-secondary text-secondary-foreground hover:bg-secondary/80 rounded-full px-4 py-1 text-xs uppercase tracking-wider font-bold">
                    {currentContent.theme}
                  </Badge>
                  <CardTitle className="text-4xl font-heading font-bold mt-2 leading-tight">
                    {currentContent.title}
                  </CardTitle>
                </div>
                <div className="text-6xl opacity-10 font-heading font-black">
                  {selectedDay < 10 ? `0${selectedDay}` : selectedDay}
                </div>
              </div>
            </CardHeader>

            <CardContent className="grid md:grid-cols-5 gap-8 pt-6">
              {/* Left Column: Content */}
              <div className="md:col-span-3 space-y-8">
                <section>
                  <h4 className="text-sm font-bold text-muted-foreground uppercase tracking-wider mb-3">Insight</h4>
                  <p className="text-lg leading-relaxed text-foreground/90 font-medium">
                    {currentContent.insight}
                  </p>
                </section>

                <section className="bg-secondary/30 p-6 rounded-2xl border border-secondary/50">
                  <h4 className="text-sm font-bold text-primary uppercase tracking-wider mb-3 flex items-center gap-2">
                    <span className="w-2 h-2 rounded-full bg-primary"></span>
                    Today's Action
                  </h4>
                  <p className="text-lg text-foreground">
                    {currentContent.action}
                  </p>
                </section>

                <section>
                  <h4 className="text-sm font-bold text-muted-foreground uppercase tracking-wider mb-3">Suggested Script</h4>
                  <blockquote className="border-l-4 border-primary pl-4 py-2 italic text-xl font-serif text-foreground/80 bg-muted/20 rounded-r-lg">
                    "{currentContent.script}"
                  </blockquote>
                </section>
              </div>

              {/* Right Column: Reflection */}
              <div className="md:col-span-2 space-y-6 flex flex-col h-full">
                <div className="bg-muted/30 p-6 rounded-2xl border border-border flex-grow flex flex-col">
                  <h4 className="text-sm font-bold text-foreground uppercase tracking-wider mb-4 flex items-center gap-2">
                    Daily Reflection
                  </h4>
                  <p className="text-sm text-muted-foreground mb-4 italic">
                    {currentContent.reflection_question}
                  </p>
                  <Textarea 
                    placeholder="Write your thoughts here..." 
                    className="flex-grow bg-background border-none resize-none focus-visible:ring-1 focus-visible:ring-primary"
                    value={user.reflections[selectedDay] || reflection}
                    onChange={(e) => setReflection(e.target.value)}
                  />
                  <div className="mt-4 flex justify-end">
                     <Button 
                      variant="ghost" 
                      size="sm" 
                      onClick={handleSaveReflection}
                      className="text-muted-foreground hover:text-primary"
                    >
                      Save Note
                    </Button>
                  </div>
                </div>

                <Button 
                  size="lg" 
                  className={`w-full rounded-full text-lg font-bold h-14 shadow-lg transition-all hover:scale-[1.02] active:scale-[0.98] ${isCompleted ? "bg-green-500 hover:bg-green-600 text-white" : "bg-primary text-primary-foreground hover:bg-primary/90"}`}
                  onClick={handleCompleteDay}
                  disabled={isCompleted}
                >
                  {isCompleted ? (
                    <>
                      <Check className="mr-2 h-5 w-5" /> Completed
                    </>
                  ) : (
                    "Mark Day Complete"
                  )}
                </Button>
              </div>
            </CardContent>
          </Card>
        </motion.div>
      </AnimatePresence>
    </div>
  );
}

